import Pencil from "https://unpkg.com/pencil.js/dist/pencil.esm.js";

const scene = new Pencil.Scene(undefined, {
    fill: "#b3f0ff",
});

const width = 100;
const height = 80;
const options = {
    fill: "#ebc31b",
    stroke: "teal",
    strokeWidth: 5,
    opacity: 0.5,
};

for (let i = 0; i < 5; ++i) {
    const randomPosition = new Pencil.Position(
        Pencil.Math.random(scene.width - (width * 2)),
        Pencil.Math.random(scene.height - (height * 2)),
    );
    const rectangle = new Pencil.Rectangle(randomPosition, width, height, options);
    rectangle.draggable({
        constrain: new Pencil.Vector([0, 0], [scene.width - width, scene.height - height])
    });
    rectangle.on("hover", () => rectangle.options.opacity = 1)
        .on("leave", () => rectangle.options.opacity = 0.5);
    scene.add(rectangle);
}

scene.startLoop();
