import { Scene, Circle } from "https://unpkg.com/pencil.js/dist/pencil.esm.js";

// Any valid CSS color is supported (named, hex, rgb, hsl ...)
const scene = new Scene(undefined, {
    fill: "#45a8ff",
});

const sun = new Circle(scene.center, 100, {
    fill: "orange",
});

// The "add" function returns the scene so we can chain calls
scene
    .add(sun)
    .render();
