import { Scene, Circle, Rectangle } from "https://unpkg.com/pencil.js/dist/pencil.esm.js";

const scene = new Scene(undefined, {
    fill: "#45a8ff",
});

const sun = new Circle(scene.center, 100, {
    fill: "orange",
});

const width = scene.width;
const height = scene.height / 2;
// Positions can be declared with a Position object or an array with [x, y] coordinates
const ocean = new Rectangle([0, scene.height / 2], width, height, {
    fill: "#28488f",
});

scene
    // Later components will overlap former components
    .add(sun, ocean)
    .render();
